import axios from "axios";
import React, { Component } from "react";
import moment from "moment";
import Weather from "./Weather";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchedCityDetails: [],
      fiveDaysDataList: [],
      activeDay: "",
      cityName: "patna",
      daysList: [],
    };
  }

  getFiveDaysWeatherReport = () => {
    axios
      .get("https://api.openweathermap.org/data/2.5/forecast", {
        params: {
          appid: "c85926f783685bd4203a4255e5b93c4f",
          lat: 51.51,
          lon: -0.13,
          cnt: 40,
        },
      })
      .then((res) => {
        const resData = res.data.list;
        let toDays = moment(resData[0].dt_txt).format("dddd");
        let daysList = [];
        var i;
        for (i = 0; i < 5; i++) {
          daysList.push(
            moment(resData[0].dt_txt).add(i, "days").format("dddd")
          );
        }
        this.setState({
          fiveDaysDataList: resData,
          activeDay: toDays,
          daysList,
        });
      });
  };

  getCityWeatherReport = () => {
    const { cityName } = this.state;
    axios
      .get("https://api.openweathermap.org/data/2.5/weather", {
        params: {
          appid: "c85926f783685bd4203a4255e5b93c4f",
          q: cityName,
        },
      })
      .then((res) => {
        const resData = res.data;
        this.setState({ searchedCityDetails: resData}, () =>
          this.getFiveDaysWeatherReport()
        );
      });
  };

  componentDidMount() {
    this.getCityWeatherReport();
  }

  handleActiveDay = (day) => {
    this.setState({ activeDay: day });
  };

  handleSearchCity = (search) => {
    this.setState({ cityName: search });
  };
  render() {
    const {
      fiveDaysDataList,
      activeDay,
      searchedCityDetails,
      daysList,
    } = this.state;
    return (
      <div className="bg">
        <div className="container">
          <div classNameName="row pt-5 ">
            <div classNameName="col-9">
              <input
                className="form-control"
                type="text"
                placeholder="Enter City Name "
                aria-label="Search"
                onChange={(e) => this.handleSearchCity(e.target.value)}
              />
            </div>
            <div classNameName="col-3">
              <button
                classNameName="search_button"
                onClick={() => this.getCityWeatherReport()}
              >
                Search
              </button>
            </div>
          </div>
          <div className="row pt-5">
            {daysList &&
              daysList.map((data, index) => {
                return (
                  <div classNameName="col " key={index}>
                    <div
                      classNameName={`card al_card ${
                        activeDay === data ? "active_card " : ""
                      }`}
                      onClick={() => this.handleActiveDay(data)}
                    >
                      <div className="card-body">
                        <h5 className="card-title text-center">{data}</h5>
                      </div>
                    </div>
                  </div>
