                              {moment(data.dt_txt).format("DD/MMM/YYYY")}
                            </div>
                          </div>
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center">
                            <div id="time">
                              {moment(data.dt_txt).format("HH A")}
                            </div>
                          </div>
                        </div>
                        <p id="description">{data.weather[0].description}</p>
                        <div className="row">
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center temp">
                            <div classNameName="detailed_report_icon">
                              <span>
                                <i
                                  classNameName={`wi wi-owm-${data.weather[0].id}`}
                                ></i>
                              </span>
                              {/* <img
                                  src={`https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRS70L-iHFmYWpM9-7w0hmw8zARwwe_CHPO1w&usqp=CAU`}
                                  classNameName="img-fluid d-block"
                                  alt="logo"
                                /> */}
                            </div>
                          </div>
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center temp">
                            <div>
                              <span id="temp">
                                {data.main.temp_max.toFixed(0)}°
                              </span>
                              <span className="temp_unit"> F</span>
                            </div>
                          </div>
                        </div>
                        <p className="hor-line"></p>
                        <div className="row">
                          <div className="col-xs-6  col-md-6 col-ms-6 text-center border-right">
                            <div className="text-left">
                              <span>
                                <i className="wi wi-strong-wind"></i> Wind:{" "}
                              </span>
                              <span id="wind">{data.wind.speed} mi/h</span>
                            </div>
                            <div className="text-left">
                              <span>
                                <i className="wi wi-windy"></i> Direction:{" "}
                              </span>
                              <span id="wind_dir">{data.wind.deg} ESE</span>
                            </div>
                          </div>
                          <div className="col-xs-6 col-ms-6 text-center">
                            <div className="text-left">
                              <span>
                                <i className="wi wi-thermometer"></i> Max:{" "}
                              </span>
                              <span id="temp_max"> {data.main.temp_max}°</span>
                              <span className="temp_unit">F</span>
                            </div>
                            <div className="text-left">
                              <span>
                                <i className="wi wi-thermometer-exterior"></i> Min:
                                &nbsp;
                              </span>
                              <span id="temp_min">{data.main.temp_min}°</span>
                              <span className="temp_unit">F</span>
                            </div>
                          </div>
                        </div>
                        <p className="hor-line"></p>

                        <div className="row">
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center border-right">
                            <div className="text-left">
                              <span>
                                <i className="wi wi-humidity"></i> Humidity:{" "}
                              </span>
                              <span id="humidity"> {data.main.humidity} %</span>
                            </div>
                            <div className="text-left">
                              <span>
                                <i className="wi wi-barometer"></i> Pressure:{" "}
                              </span>
                              <span id="pressure">{data.main.pressure}</span>
                            </div>
                          </div>
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center">
                            <div className="text-left">
                              <i className="wi wi-sunrise"></i> Sunrise:{" "}
                              <span id="sunrise">
                                {moment(
                                  new Date(
                                    searchedCityDetails.sys.sunrise * 1000
                                  )
                                ).format("HH:MM A")}{" "}
                              </span>
                            </div>
                            <div className="text-left">
                              <i className="wi wi-sunset"></i> Sunset: &nbsp;
                              <span id="sunset">
                                {moment(
                                  new Date(
                                    searchedCityDetails.sys.sunset * 1000
                                  )
                                ).format("HH:MM A")}
                              </span>
                            </div>
                          </div>
                        </div>
                        <p className="hor-line"></p>
                        <div className="row">
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center border-right">
                            <div className="text-left">
                              <span>Sea Level: </span>
                              <span id="sea_level">
                                {" "}
                                {data.main.sea_level} %
                              </span>
                            </div>
                          </div>
                          <div className="col-xs-6 col-md-6 col-ms-6 text-center">
                            <div className="text-left">
                              Ground Level:{" "}
                              <span id="grnd_level">
                                {data.main.grnd_level}{" "}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </>
                );
              })}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
